mapper.insert:
INSERT INTO comment
SET contents = #{contents}
  , writer_id = #{writerId}
  , board_id = #{boardId}

mapper.delete:
DELETE FROM comment
WHERE comment_id = #{commentId}
AND writer_id = #{writerId}

mapper.update:
UPDATE comment
  SET contents = #{contents}
WHERE comment_id = #{commentId}
  AND writer_id = #{writerId}

mapper.select:
SELECT CEIL COUNT(comment_id) / #{len}
FROM comment
WHERE board_id = #{boardId}
